var searchFunc=function(path,search_id,content_id){var $input=document.getElementById(search_id);var $resultContent=document.getElementById(content_id);$resultContent.innerHTML='<div class="m-auto text-center">Loading...</div>';$.ajax({url:path,dataType:"xml",success:function(xmlResponse){var dataList=$("entry",xmlResponse).map(function(){return{title:$("title",this).text(),content:$("content",this).text(),url:$("url",this).text()}}).get();$resultContent.innerHTML="";$input.addEventListener("input",function(){var str="";var keywords=this.value.trim().toLowerCase().split(/[\s-]+/);$resultContent.innerHTML="";if(this.value.trim().length<=0){return}dataList.forEach(function(data){var isMatch=true;if(!data.title||data.title.trim()===""){data.title="Untitled"}var orig_data_title=data.title.trim();var data_title=orig_data_title.toLowerCase();var orig_data_content=data.content.trim().replace(/<[^>]+>/g,"");var data_content=orig_data_content.toLowerCase();var data_url=data.url;var index_title=-1;var index_content=-1;var first_occur=-1;if(data_content!==""){keywords.forEach(function(keyword,i){index_title=data_title.indexOf(keyword);index_content=data_content.indexOf(keyword);if(index_title<0&&index_content<0){isMatch=false}else{if(index_content<0){index_content=0}if(i===0){first_occur=index_content}}})}else{isMatch=false}if(isMatch){str+="<a href='"+data_url+"' class='list-group-item list-group-item-action font-weight-bolder search-list-title'>"+orig_data_title+"</a>";var content=orig_data_content;if(first_occur>=0){var start=first_occur-20;var end=first_occur+80;if(start<0){start=0}if(start===0){end=100}if(end>content.length){end=content.length}var match_content=content.substring(start,end);keywords.forEach(function(keyword){var regS=new RegExp(keyword,"gi");match_content=match_content.replace(regS,'<span class="search-word">'+keyword+"</span>")});str+="<p class='search-list-content'>"+match_content+"...</p>"}}});const input=$("#local-search-input");if(str.indexOf("list-group-item")===-1){return input.addClass("invalid").removeClass("valid")}input.addClass("valid").removeClass("invalid");$resultContent.innerHTML=str})}});$(document).on("click","#local-search-close",function(){$("#local-search-input").val("").removeClass("invalid").removeClass("valid");$("#local-search-result").html("")})};